SELECT * 
FROM (SELECT ROWNUM RNUM , P.*
	FROM (SELECT * FROM PRODUCT_TBL
		WHERE 

			P_NAME || P_SELLER || P_DESCRIPTION
			LIKE #{search_word}



			  	P_NAME
			  	P_SELLER
			  	P_DESCRIPTION

			LIKE #{search_word}

	ORDER BY P_READCOUNT) P
	)
WHERE RNUM >= #{start} AND RNUM <= #{end}

SELECT COUNT(*) FROM
CODE_IDENTIFY
WHERE FIND_CODE = #{USER_ID}
