mapper.update:
update USER_TBL
set USER_NAME = #{USER_NAME}, USER_PHONE = #{USER_PHONE}, USER_EMAIL = #{USER_EMAIL}, USER_POSTCODE = #{USER_POSTCODE}, USER_ADDRESS = #{USER_ADDRESS}
WHERE USER_ID = #{USER_ID}

mapper.select:
select USER_PASSWORD
from USER_TBL
WHERE USER_ID = #{USER_ID}

